import React from "react";
import TshirtData from "./TshirtData";

const Tshirt = (props) => {
  const { list, onSelectTshirt } = props;

  const handleButtonClick = (id, size) => {
    const selectedTshirt = list.find(tshirt => tshirt.id === id);
    onSelectTshirt(selectedTshirt, size);
  };

  return (
    <div>
      <ul>
        {list.map(item => (
          <div key={item.id}>
            <li>
              <TshirtData
                name={item.name}
                description={item.description}
                price={item.price}
              />
            </li>
            <div>
              <button onClick={() => handleButtonClick(item.id, 'L')}>Large</button>
              <button onClick={() => handleButtonClick(item.id, 'M')}>Medium</button>
              <button onClick={() => handleButtonClick(item.id, 'S')}>Small</button>
            </div>
          </div>
        ))}
      </ul>
    </div>
  );
};

export default Tshirt;
