import React from "react";
const Form = (props) => {
    const { selectedTshirt, selectedSize } = props;
  
    return (
      <form>
        {selectedTshirt && (
          <React.Fragment>
            <div>
              <label htmlFor="name">Name:</label>
              <input
                type="text"
                id="name"
                value={selectedTshirt.name}
                readOnly
              />
            </div>
            <div>
              <label htmlFor="description">Description:</label>
              <input
                type="text"
                id="description"
                value={selectedTshirt.description}
                readOnly
              />
            </div>
            <div>
              <label htmlFor="price">Price:</label>
              <input
                type="number"
                id="price"
                value={selectedTshirt.price}
                readOnly
              />
            </div>
            <div>
              <label htmlFor="selectedSize">Selected Size:</label>
              <input
                type="text"
                id="selectedSize"
                value={selectedSize}
                readOnly
              />
            </div>
          </React.Fragment>
        )}
      </form>
    );
  };
  
  export default Form;
