import React, { useState } from "react";

const Tshirt = (props) => {
    const initialTshirtState = props.list.map(item => ({
        ...item,
        Lquantity: 100,
        Mquantity: 150,
        Squantity: 200
    }));

    const [tshirts, setTshirts] = useState(initialTshirtState);

    const ButtonHandler = (id, size) => {
        // Find the selected T-shirt
        const selectedTshirt = tshirts.find(tshirt => tshirt.id === id);
        let quantity;
        // Update the quantity and determine which size was clicked
        switch(size) {
            case 'L':
                quantity = selectedTshirt.Lquantity;
                setTshirts(tshirts.map(tshirt =>
                    tshirt.id === id ? { ...tshirt, Lquantity: tshirt.Lquantity - 1 } : tshirt
                ));
                break;
            case 'M':
                quantity = selectedTshirt.Mquantity;
                setTshirts(tshirts.map(tshirt =>
                    tshirt.id === id ? { ...tshirt, Mquantity: tshirt.Mquantity - 1 } : tshirt
                ));
                break;
            case 'S':
                quantity = selectedTshirt.Squantity;
                setTshirts(tshirts.map(tshirt =>
                    tshirt.id === id ? { ...tshirt, Squantity: tshirt.Squantity - 1 } : tshirt
                ));
                break;
            default:
                quantity = 0;
        }
        // Log the selected T-shirt and quantity
        props.selectedTshirt(`Selected T-shirt: ${selectedTshirt.name}, Size: ${size}, Quantity: ${quantity}`);
    }

    return (
        <div>
            <ul>
                {tshirts.map(item => (
                    <div key={item.id}>
                        <li>
                            <div>{item.name}-{item.description}-{item.price}</div>
                        </li>
                        <div>
                            <button onClick={() => ButtonHandler(item.id, 'L')}>Large {item.Lquantity}</button>
                            <button onClick={() => ButtonHandler(item.id, 'M')}>Medium {item.Mquantity}</button>
                            <button onClick={() => ButtonHandler(item.id, 'S')}>Small {item.Squantity}</button>
                        </div>
                    </div>
                ))}
            </ul>
        </div>
    );
};

export default Tshirt;
