import React, { useState } from "react";

const Tshirt = (props) => {
  const [quantity, setQuantity] = useState({
    Lquantity: 200,
    Mquantity: 100,
    Squantity: 300
  });

  const LargeButtonHandler = (id) => {
    setQuantity((prevQuantity) => ({
      ...prevQuantity,
      Lquantity: prevQuantity.Lquantity - 1
    }));
  };
  const MediumButtonHandler = (id) => {
    setQuantity((prevQuantity) => ({
      ...prevQuantity,
      Lquantity: prevQuantity.Lquantity - 1
    }));
  };
  const SmallButtonHandler = (id) => {
    setQuantity((prevQuantity) => ({
      ...prevQuantity,
      Lquantity: prevQuantity.Lquantity - 1
    }));
  };
  return (
    <div>
      {props.list.map((items) => (
        <div key={items.id}>
          <li>
            <div>{items.name}-{items.description}-{items.price}</div>
          </li>
          <button onClick={() => LargeButtonHandler(items.id)}>Large {quantity.Lquantity}</button>
          <button>Medium</button>
          <button>Small</button>
        </div>
      ))}
    </div>
  );
};

export default Tshirt;
