import Tshirt from "./components/Tshirt"
import React, { useState } from "react"
const App = () =>{
  const[ChooseTshirt,setChooseTshirt]=useState(null)
  const tshirts = [
    {id:1,name:'Cotton-Candy',description:'Pure Cotton Shirt',price:250},
    {id:2,name:'Nike',description:'Stylish Shirt',price:500},
    {id:3,name:'Addidas',description:'Sports Shirt',price:1000},
    {id:4,name:'Puma',description:'Branded Shirt',price:1500}
  ]
  return(
    <div>
      <h1>Tshirt Shopping Site</h1>
      <button>Cart</button>
      <Tshirt list={tshirts} selectedTshirt={setChooseTshirt} />
    </div>
  )

}
export default App
