const Form = (props) => {
    const FormHandler = (event) =>{
        event.preventDefault()
    }
    return (
      <form onSubmit={FormHandler}>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            value={props.selectedTshirt.name}
            readOnly
          />
        </div>
        <div>
          <label htmlFor="description">Description:</label>
          <input
            type="text"
            id="description"
            value={props.selectedTshirt.description}
            readOnly
          />
        </div>
        <div>
          <label htmlFor="price">Price:</label>
          <input
            type="number"
            id="price"
            value={props.selectedTshirt.price}
            readOnly
          />
        </div>
      </form>
    );
  };
  
  export default Form;
