import React, { useState } from "react";

const Tshirt = (props) => {
  const [quantity, setQuantity] = useState({
    Lquantity: 200,
    Mquantity: 100,
    Squantity: 300
  });

  const LargeButtonHandler = (id) => {
    setQuantity((prevQuantity) => ({
      ...prevQuantity,
      Lquantity: prevQuantity.Lquantity > 0 ? prevQuantity.Lquantity - 1 : 0
    }));
  };

  const MediumButtonHandler = (id) => {
    setQuantity((prevQuantity) => ({
      ...prevQuantity,
      Mquantity: prevQuantity.Mquantity > 0 ? prevQuantity.Mquantity - 1 : 0
    }));
  };

  const SmallButtonHandler = (id) => {
    setQuantity((prevQuantity) => ({
      ...prevQuantity,
      Squantity: prevQuantity.Squantity > 0 ? prevQuantity.Squantity - 1 : 0
    }));
  };

  return (
    <div>
      {props.list.map((items) => (
        <div key={items.id}>
          <li>
            <div>{items.name} - {items.description} - {items.price}</div>
          </li>
          <button onClick={() => LargeButtonHandler(items.id)}>
            Large ({quantity.Lquantity})
          </button>
          <button onClick={() => MediumButtonHandler(items.id)}>
            Medium ({quantity.Mquantity})
          </button>
          <button onClick={() => SmallButtonHandler(items.id)}>
            Small ({quantity.Squantity})
          </button>
        </div>
      ))}
    </div>
  );
};

export default Tshirt;
