// Form.js

import React from 'react';
import './Form.css'; // Import CSS file

const Form = (props) => {
  const { selectedTshirt, clickInfo } = props;

  const getClickInfo = () => {
    if (!selectedTshirt || !clickInfo[selectedTshirt.id]) return '';
    const info = clickInfo[selectedTshirt.id];
    return `L: ${info.L}, M: ${info.M}, S: ${info.S}`;
  };

  return (
    <form className="form-container">
      <div>
        <label htmlFor="name" className="form-label">Name:</label>
        <input
          type="text"
          id="name"
          value={selectedTshirt ? selectedTshirt.name : ''}
          readOnly
          className="form-input"
        />
      </div>
      <div>
        <label htmlFor="description" className="form-label">Description:</label>
        <input
          type="text"
          id="description"
          value={selectedTshirt ? selectedTshirt.description : ''}
          readOnly
          className="form-input"
        />
      </div>
      <div>
        <label htmlFor="price" className="form-label">Price:</label>
        <input
          type="number"
          id="price"
          value={selectedTshirt ? selectedTshirt.price : ''}
          readOnly
          className="form-input"
        />
      </div>
      <div>
        <label htmlFor="buttonInfo" className="form-label">Button Click Info:</label>
        <input
          type="text"
          id="buttonInfo"
          value={getClickInfo()}
          readOnly
          className="form-input"
        />
      </div>
      <button type="button" onClick={props.onChoose} className="form-button">Add Product</button>
    </form>
  );
};

export default Form;
