import TshirtData from "./TshirtData"
import React,{useState} from "react"
const Tshirt = (props) => {
  const[initialQantity,setInitialQuantity]={
    Lquantity:100,
    Mquantity:150,
    Squantity:200
  }
  return (
    <div>
        {props.data.map((item) => (
          <div>
            <li key={item.id}>
              <TshirtData
                name={item.name}
                description={item.description}
                price={item.price}
              />
            </li>
            <button onClick={()=>{LargeButtonClickHandler(item.id,'L')}}>Large {Lquantity}</button>
            <button>Medium {Mquantity}</button>
            <button>Small {Squantity}</button>
          </div>
        ))}
    </div>
  )
}

export default Tshirt
