import React from 'react';

const Display = (props) => {
  // Calculate total for each type of shirt
  const totals = {};

  props.data.forEach((item) => {
    const key = `${item.name}-${item.id}`;
    if (!totals[key]) {
      totals[key] = {
        name: item.name,
        id: item.id,
        L: 0,
        M: 0,
        S: 0,
        total: 0,
      };
    }
    totals[key][item.size] += 1;
    totals[key].total += item.price;
  });

  // Calculate grand total
  const grandTotal = Object.values(totals).reduce((total, item) => total + item.total, 0);

  return (
    <div>
      <header>
        <ul>
          {Object.values(totals).map((item, index) => (
            <li key={index}>
              {item.name} - {item.L}L {item.M}M {item.S}S - {item.total}
            </li>
          ))}
        </ul>
      </header>
      <div>
        Grand Total {grandTotal}
      </div>
      <footer>
        <button>Okay</button>
        <button>Cancel</button>
      </footer>
    </div>
  );
};

export default Display;
