import React from 'react';

const Display = (props) => {
  // Calculate total price and quantity for each type of shirt
  const summary = props.data.reduce((summary, shirt) => {
    const key = `${shirt.name}-${shirt.size}`;
    if (!summary[key]) {
      summary[key] = {
        name: shirt.name,
        size: shirt.size,
        totalPrice: shirt.price,
        totalQuantity: 1
      };
    } else {
      summary[key].totalPrice += shirt.price;
      summary[key].totalQuantity += 1;
    }
    return summary;
  }, {});

  return (
    <div>
      {Object.values(summary).map((shirt, index) => (
        <div key={index}>
          <p>{shirt.name} {shirt.totalQuantity}{shirt.size} - {shirt.totalPrice}</p>
        </div>
      ))}
      <p>Total: {props.data.reduce((total, shirt) => total + shirt.price, 0)}</p>
    </div>
  );
};

export default Display;
