import React from 'react';

const Display = (props) => {
  // Group shirts by name
  const groupedShirts = props.data.reduce((groups, shirt) => {
    if (!groups[shirt.name]) {
      groups[shirt.name] = [];
    }
    groups[shirt.name].push(shirt);
    return groups;
  }, {});

  return (
    <div>
      {Object.keys(groupedShirts).map(name => (
        <div key={name}>
          <h3>{name}</h3>
          <div style={{ display: 'flex' }}>
            {groupedShirts[name].map(shirt => (
              <div key={shirt.id} style={{ margin: '0 10px' }}>
                <ul>
                  <li>
                    {shirt.size}: {shirt.price}
                  </li>
                </ul>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Display;
