import React, { useState } from "react";

const Tshirt = (props) => {
  const [sizes, setSizes] = useState([
    { id: "L", name: "Large", quantity: 200 },
    { id: "M", name: "Medium", quantity: 100 },
    { id: "S", name: "Small", quantity: 300 }
  ]);

  const handleButtonClick = (id) => {
    const updatedSizes = sizes.map((size) => {
      if (size.id === id && size.quantity > 0) {
        return { ...size, quantity: size.quantity - 1 };
      }
      return size;
    });

    setSizes(updatedSizes);
  };

  return (
    <div>
      {props.list.map((item) => (
        <div key={item.id}>
          <li>
            <div>
              {item.name} - {item.description} - {item.price}
            </div>
          </li>
          {sizes.map((size) => (
            <button
              key={size.id}
              onClick={() => handleButtonClick(size.id)}
              disabled={size.quantity === 0}
            >
              {size.name} ({size.quantity})
            </button>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Tshirt;
