import { useState } from "react";
import TshirtData from "./TshirtData";

const Tshirt = (props) => {
    // Initialize state with quantities for each T-shirt
    const initialTshirtState = props.list.map(item => ({
        ...item,
        Lquantity: 100,
        Mquantity: 150,
        Squantity: 200
    }));

    const [tshirts, setTshirts] = useState(initialTshirtState);

    const LargeButtonHandler = (id) => {
        setTshirts(tshirts.map(tshirt => 
            tshirt.id === id ? { ...tshirt, Lquantity: tshirt.Lquantity - 1 } : tshirt
        ));
    }

    const MediumButtonHandler = (id) => {
        setTshirts(tshirts.map(tshirt => 
            tshirt.id === id ? { ...tshirt, Mquantity: tshirt.Mquantity - 1 } : tshirt
        ));
    }

    const SmallButtonHandler = (id) => {
        setTshirts(tshirts.map(tshirt => 
            tshirt.id === id ? { ...tshirt, Squantity: tshirt.Squantity - 1 } : tshirt
        ));
    }

    return (
        <div>
            <ul>
                {tshirts.map(item => (
                    <div key={item.id}>
                        <li>
                            <div>{item.name}-{item.description}-{item.price}</div>
                        </li>
                        <div>
                            <button onClick={() => LargeButtonHandler(item.id)}>Large {item.Lquantity}</button>
                            <button onClick={() => MediumButtonHandler(item.id)}>Medium {item.Mquantity}</button>
                            <button onClick={() => SmallButtonHandler(item.id)}>Small {item.Squantity}</button>
                        </div>
                    </div>
                ))}
            </ul>
        </div>
    );
};

export default Tshirt;
