import { useState } from "react";
import TshirtData from "./TshirtData";

const Tshirt = (props) => {
    const[Lquantity,setLquantity] = useState(100)
    const[Mquantity,setMquantity] = useState(150)
    const[Squantity,setSquantity] = useState(200)
    return (
        <div>
            <ul>
                {props.list.map(item => (
                    <div>
                        <li key={item.id}>
                            <TshirtData
                                name={item.name}
                                description={item.description}
                                price={item.price}
                            />
                        </li>
                        <div>
                            <button onClick={LargeButtonHandler}>Large {Lquantity}</button>
                            <button onClick={MediumButtonHandler}>Medium {Mquantity}</button>
                            <button onClick={SmallButtonHandler}>Small {Squantity}</button>
                        </div>
                    </div>
                ))}

            </ul>
        </div>
    );
};

export default Tshirt;
